mapper.select:
select attachs
from mccb_attachs_code
where model = #{model}
  and code = #{code}

select

from mccb_attachs_data
where model = #{model}
and name = #{name}
and note = #{note}

select

from mccb_outside_attachs

    and series = #{series}
    and inm = #{inm}
    and icu = #{icu}
    and poles = #{poles}
    and trip = #{trip}
    and special_use = #{specialUse}
    and attach_name = #{attachName}
    and note = #{note}

select

from mccb_outside_attachs a left join user u on a.creator_id = u.id

    and a.model = #{model}
    and a.name = #{name}
    and a.note = #{note}
    and b.name = #{creator}
    and b.name = #{modifier}

limit ${currentPage * pageSize}, #{pageSize}

select

from mccb_attachs_data

    and model = #{model}
    and name = #{name}
    and note = #{note}
    and creator_id = #{creatorId}
    and modifier_id = #{modifierId}

limit ${currentPage * pageSize}, #{pageSize}

select

from mccb_attachs_code

    and model = #{model}
    and code = #{code}
    and attachs = #{attachs}
    and creator_id = #{creatorId}
    and modifier_id = #{modifierId}

limit ${currentPage * pageSize}, #{pageSize}
